public class UsertaskPeer 
    extends com.sns.scout.managers.BaseUsertaskPeer
{

	static public Usertask mappingExists(int id) {
		Usertask retVal = null;
	
		try {
			if (!Torque.isInit()) {
				Torque.init(Constants.TorqueHome);
			}
			
			Criteria crit = new Criteria();
	
			/* Only use for search if the string is not empty */
			crit.add(BaseUsertaskPeer.TABLE_NAME, "taskID", id,
					Criteria.EQUAL);
	
			crit.setAll();
			List result = BaseUsertaskPeer.doSelect(crit);
			if (result.size() > 0) {
				retVal = (Usertask) result.get(0);
			}
		} catch (TorqueException e) {
			System.out.println("Exception initializing torque.");
			e.printStackTrace();
		} catch (Exception ex) {
			ex.printStackTrace();
		}
		return retVal;
	
	}
}
